<template>    <div class="main-content">        <v-container grid-list text-center>            <div v-for="value in home"                 v-bind:key="value.text">                <v-layout collumn v-for="homeText in value" :key="homeText.id" class="home-content">                    <v-flex xs12 md5 class="img">                        <div><img :src="homeText.image" class="home-img"/></div>                    </v-flex>                    <v-flex xs12 md8 class="home-text-block">                        <h1 class="main-heading">{{ homeText.mainTitle }}</h1>                        <p class="home-subtitle">{{ homeText.subtitle }}</p>                        <div class="home-description" v-html="homeText.description"></div>                        <div>                            <v-btn to="/quiz" class="button-main">{{ homeText.button }}                            </v-btn>                        </div>                        <p class="text-caption">{{ homeText.caption }}</p>                    </v-flex>                </v-layout>                <button type="button" class="btn" @click="showModal">                    Open Modal!                </button>                <modal v-show="isModalVisible" @close="closeModal" />            </div>            <footers></footers>        </v-container>    </div></template><script>  import Footer from './Footer'  import Modal from './Modal'  export default {    /* eslint-disable */    name: 'Homepage',    components: {      footers: Footer,    },    data() {      return {        home: [],        isModalVisible: false,      }    },    computed: {      links() {        return [          {title: 'Home', url: '/'},          {title: 'Quiz', url: '/quiz'},        ]      }    },    created() {      this.$http.get('api/homepage.json')          .then(response => {                return response.json();                // for (let home in response.data) {                //     this.homepage.push(response.data[home])                // }                //console.log(response)              }, response => {                console.log(response)              }          )          .then(home => {            this.home = home;            //console.log(home);          })      ;    },    methods: {      showModal() {        this.isModalVisible = true;      },      closeModal() {        this.isModalVisible = false;      },      close(event) {        this.$emit('close');      },    },  }</script><!-- Add "scoped" attribute to limit CSS to this component only --><style scoped></style>